"use strict";
const body = document.querySelector('body');

body.addEventListener('click', (e) => {

   const btnBurger = document.querySelector(".header__btn-icon-burger");
   const btnClose = document.querySelector(".header__btn-icon-close");

   const btnMenu = document.querySelector('.header__button-menu');
   const clickMenu = e.composedPath().includes(btnMenu);

   if (clickMenu) {
      e.target.closest('.header__menu').querySelector(".header__links").classList.toggle('header__links--disaibled');
      e.target.closest('.header__menu').querySelector(".header__btn-icon-burger").classList.toggle('header__btn-icon-burger--disaibled');
      e.target.closest('.header__menu').querySelector(".header__btn-icon-close").classList.toggle('header__btn-icon-close--disaibled');
   };

   const headerMenu = document.querySelector('.header__links');
   const menu = e.composedPath().includes(headerMenu);

   if (!menu && !clickMenu) {
      headerMenu.classList.add('header__links--disaibled');
      btnBurger.classList.remove('header__btn-icon-burger--disaibled');
      btnClose.classList.add('header__btn-icon-close--disaibled');
   };
});


